fn main() {}

fn not_quite_lisp(input: &str) -> Option<usize> {
    let mut counter = 0;

    for (idx, c) in input.chars().enumerate() {
        match c {
            '(' => counter += 1,
            ')' => counter -= 1,
            _ => unreachable!(),
        }

        if counter == -1 {
            return Some(idx + 1);
        }
    }

    None
}

#[test]
fn test_1() {
    assert_eq!(not_quite_lisp("(())"), None);
}

#[test]
fn test_2() {
    assert_eq!(not_quite_lisp("()()"), None);
}

#[test]
fn test_3() {
    assert_eq!(not_quite_lisp("((("), None);
}

#[test]
fn test_4() {
    assert_eq!(not_quite_lisp("(()(()("), None);
}

#[test]
fn test_5() {
    assert_eq!(not_quite_lisp("))((((("), Some(1));
}

#[test]
fn test_6() {
    assert_eq!(not_quite_lisp("())"), Some(3));
}

#[test]
fn test_7() {
    assert_eq!(not_quite_lisp("))("), Some(1));
}

#[test]
fn test_8() {
    assert_eq!(not_quite_lisp(")))"), Some(1));
}

#[test]
fn test_9() {
    assert_eq!(not_quite_lisp(")())())"), Some(1));
}

