fn main() {}

fn not_quite_lisp(input: &str) -> i32 {
    let mut counter = 0;

    for c in input.chars() {
        match c {
            '(' => counter += 1,
            ')' => counter -= 1,
            _ => unreachable!(),
        }
    }

    counter
}

#[test]
fn test_1() {
    assert_eq!(not_quite_lisp("(())"), 0);
}

#[test]
fn test_2() {
    assert_eq!(not_quite_lisp("()()"), 0);
}

#[test]
fn test_3() {
    assert_eq!(not_quite_lisp("((("), 3);
}

#[test]
fn test_4() {
    assert_eq!(not_quite_lisp("(()(()("), 3);
}

#[test]
fn test_5() {
    assert_eq!(not_quite_lisp("))((((("), 3);
}

#[test]
fn test_6() {
    assert_eq!(not_quite_lisp("())"), -1);
}

#[test]
fn test_7() {
    assert_eq!(not_quite_lisp("))("), -1);
}

#[test]
fn test_8() {
    assert_eq!(not_quite_lisp(")))"), -3);
}

#[test]
fn test_9() {
    assert_eq!(not_quite_lisp(")())())"), -3);
}

